import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function UrlRedirector() {
  const { shortUrl } = useParams();
  //const navigate = useNavigate();
  const [error, setError] = useState("");

  useEffect(() => {
    const getLongUrlAndRedirect = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/urls/${shortUrl}`
        );
        window.location.href = response.data;
      } catch (error) {
        setError(
          "Failed to redirect: URL may not exist or the server is unavailable."
        );
      }
    };
    getLongUrlAndRedirect();
  }, [shortUrl]);

  return (
    <div>
      {error ? (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      ) : (
        <p>Redirecting...</p>
      )}
    </div>
  );
}
export default UrlRedirector;
