import React, { useState } from "react";

function UrlDecoder() {
  const [shortUrlCode, setShortUrlCode] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    const verifyUrl = `http://localhost:8080/api/urls/${shortUrlCode}`;

    try {
      const response = await fetch(verifyUrl, {
        method: "GET", // Or another method if you're using one to verify
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`, // Include JWT token if required
        },
      });

      if (!response.ok) {
        throw new Error("Short URL not found or invalid");
      }

      // Assuming the endpoint redirects successfully, or you change this to handle success differently
      window.location.href = verifyUrl;
    } catch (error) {
      // Parse the JSON error message and set it to state
      const errorData = await error.response.json(); // Make sure the response is actually JSON
      setErrorMessage(
        errorData.message || "An error occurred. Please try again later."
      );
    }
  };

  return (
    <div className="container">
      <h2>Decode Short URL</h2>
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="shortUrlCode">Short URL Code</label>
          <input
            type="text"
            className="form-control"
            id="shortUrlCode"
            placeholder="Enter short URL code (e.g., kzwXX)"
            value={shortUrlCode}
            onChange={(e) => setShortUrlCode(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Decode
        </button>
      </form>
    </div>
  );
}

export default UrlDecoder;
